import React, { useState } from "react"
import api from "../api"

const Users = () => {
    const [users, setUsers] = useState(api.users.fetchAll())
    const handleDelete = (userId) => {        
        setUsers(users.filter(item=>item.userId !== userId))
        
        // const newList = users.filter((item) => item.userId !== userId)
        // setUsers(newList)
        console.log('userId', userId)
    }

    const renderPhrase = (number) => {
        console.log(users.length)
        if(users.length === 4) {
            return <span className="badge bg-primary">{ `${users.length} человека тусанут с тобой сегодня` }</span>  
        }  
        if(users.length === 1) {
            return <span className="badge bg-primary">{ `${users.length} человек тусанет с тобой сегодня` }</span>
        }
        if(users.length === 0) {
            return <span className="badge bg-danger">{ "Никто с тобой не тусанет" }</span>
        }
              
        return <span className="badge bg-primary">{ `${users.length} человек тусанет с тобой сегодня` }</span>  
    }
    // console.log(api.users.fetchAll())
    
    return <>
    <h2>{renderPhrase()}</h2>
    <table className="table">
  <thead>
    <tr>      
      <th scope="col">Имя</th>
      <th scope="col">Качества</th>
      <th scope="col">Профессия</th>
      <th scope="col">Встретился, раз</th>
      <th scope="col">Оценка</th>
    </tr>
  </thead>
  <tbody>
    {
    users.map((user) => {
        return(<tr key={user._id}>
        <td>{user.name}</td> 
        
        <td>             
            {
                user.qualities.map((qualitie) => {   
                        const handleQualiti = () => {
                            return(
                                {qualitie}
                            )
                        }
                })
            }                
        </td>
        <td>{anObjectMapped.profession.name}</td>
        <td>{anObjectMapped.completedMeetings}</td>  
        <td>{anObjectMapped.rate}/5</td>  
        <button type="button" className="btn btn-outline-danger" onClick={() => handleDelete(users._id)}>
            Remove
        </button>           
    </tr>)

    })
            
}
  </tbody>
</table>


    </>
}

export default Users
