import React, { useState } from "react";
import api from "../api";

const Users = () => {
  const [users, setUsers] = useState(api.users.fetchAll());
  const handleDelete = (userId) => {
    setUsers(users.filter((user) => user._id !== userId));

    // const newList = users.filter((item) => item.userId !== userId)
    // setUsers(newList)
    console.log("userId", userId);
    // console.log('Id', _id)
  };

  const renderPhrase = (number) => {
    console.log(users.length);
    if (number === 4 || number === 2 || number === 3) {
      return (
        <span className="badge bg-primary">{`${users.length} человека тусанут с тобой сегодня`}</span>
      );
    }
    if (number === 1 || number > 4) {
      return (
        <span className="badge bg-primary">{`${users.length} человек тусанет с тобой сегодня`}</span>
      );
    }
    if (number === 0) {
      return (
        <span className="badge bg-danger">{"Никто с тобой не тусанет"}</span>
      );
    }
    
      `${users.length} человек тусанет с тобой сегодня`
    
  };
  // console.log(api.users.fetchAll())

  return (
    <>
      <h2>{renderPhrase()}</h2>
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Имя</th>
            <th scope="col">Качества</th>
            <th scope="col">Профессия</th>
            <th scope="col">Встретился, раз</th>
            <th scope="col">Оценка</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => {
            return (
              <tr key={users._id}>
                <td>{user.name}</td>

                <td>
                  {user.qualities.map((qualitie) => {
                    const handleQualiti = () => {
                      return `badge ms-2 bg-${qualitie.color}`;
                    };
                    return (
                      <span className={handleQualiti()}>{qualitie.name}</span>
                    );
                  })}
                </td>
                <td>{user.profession.name}</td>
                <td>{user.completedMeetings}</td>
                <td>{user.rate}/5</td>
                <td>
                  <button
                    className="btn btn-outline-danger"
                    onClick={() => handleDelete(users._id)}
                  >
                    Remove
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
};

export default Users;
