import React, { useState } from "react"
import api from "../api"
// import { useState } from "react"

const Users = () => {
    const [users, setUsers] = useState(api.users.fetchAll())
    const handleDelete = (userId) => {        
        setUsers(prevState=>prevState.filter(tag=>tag!==userId))      
        
        this.setState(state => {
            const { items } = state;
            const filteredItems = items.filter(item => item.id !== itemId);
            return {
              items: filteredItems
            };
          });
    }

    // const renderUsers = () => {
    //      return users.length !== 0 ? users.map(tag=>(
    //         <button
    //              key={tag} 
    //              className="btn btn-primary btn-sm m-2" 
    //              onClick={()=>handleDelete(tag)}>
    //              {tag}
    //         </button>
    //         )) : "No Tags"
    // }
    // if(users.length!==0){
    //     return <ul> {renderUsers()}</ul>
    // }
    const renderPhrase = (number) => {
        console.log(users.length)
        return <span className="badge bg-primary">{ `${users.length} человек тусанет с тобой сегодня` }</span>          
    }
    console.log(api.users.fetchAll())
    return <>
    <h2>{renderPhrase()}</h2>
    <table className="table">
  <thead>
    <tr>      
      <th scope="col">Имя</th>
      <th scope="col">Качества</th>
      <th scope="col">Профессия</th>
      <th scope="col">Встретился, раз</th>
      <th scope="col">Оценка</th>
    </tr>
  </thead>
  <tbody>
    {
    users.map(anObjectMapped => {return (
            <tr key={`${anObjectMapped.name}`}>
                <td>{anObjectMapped.name}</td> 
                
                <td>             
                    {
                        anObjectMapped.qualities.map(anObjectMappedByQualities => { return (
                            
                               <span key={`${anObjectMappedByQualities.name}`} 
                               className={`badge bg-${anObjectMappedByQualities.color} m-1`}>                                   
                                   {anObjectMappedByQualities.name}
                                </span> 
                            
                        )
                        })
                    }                
                </td>
                <td>{anObjectMapped.profession.name}</td>
                <td>{anObjectMapped.completedMeetings}</td>  
                <td>{anObjectMapped.rate}/5</td>                
            </tr>)
    })
}
  </tbody>
</table>


    </>
}

export default Users
